package com.javaex.practice;

public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1에서 4까지 +1 출력, 10에서 2까지 -2 출력
		for(int i=1;i<5;i++) {
			System.out.println(i);
		}
		
		System.out.println("================");
		
		for(int i=10;i>0;i=i-2) {
			System.out.println(i);
		}

	}

}
