package com.javaex.practice;

public class Ex08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j;
		
		for(i=1;i<=9;i++) {
			for(j=2;j<=9;j++) {
				System.out.print(j + "*" + i + "=" + j*i +"\t");
			}
			System.out.println("");
		}

	}

}
